import React from 'react';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';

// Usa la imagen en Base64
const imgUrl = '';

const DescargarPDFButton = ({ solicitud }) => {
  const generarPDF = () => {
    const doc = new jsPDF();

    // Añadir imagen de encabezado
    doc.addImage(imgUrl, 'PNG', 10, 10, 190, 30);

    // Mover el cursor de escritura después de la imagen
    doc.setFontSize(16);
    doc.text("Solicitud de Cambio", 10, 50);

    // Añadir cuadros y texto con formato
    doc.setFontSize(12);
    doc.setLineWidth(0.1);

    // Datos de la solicitud en cuadros
    const marginX = 10;
    const marginY = 60;
    const lineHeight = 10;
    const boxHeight = lineHeight + 2;

    const fields = [
      { label: 'ID', value: solicitud.id },
      { label: 'Nombre del Solicitante', value: solicitud.nombre },
      { label: 'Fecha', value: solicitud.fecha },
      { label: 'Descripción', value: solicitud.descripcion },
      { label: 'Nombre del Proyecto', value: solicitud.nombreProyecto },
      { label: 'Justificación', value: solicitud.justificacion },
      { label: 'Prioridad', value: solicitud.prioridad },
      { label: 'Estado', value: solicitud.estado },
    ];

    fields.forEach((field, index) => {
      const posY = marginY + index * boxHeight;

      // Dibujar el cuadro
      doc.rect(marginX, posY, 190, boxHeight);

      // Añadir el texto
      doc.text(`${field.label}: ${field.value}`, marginX + 5, posY + lineHeight);
    });

    doc.save(`solicitud_${solicitud.id}.pdf`);
  };

  return (
    <button onClick={generarPDF}>Descargar PDF</button>
  );
};

export default DescargarPDFButton;
